/** 
 * Calculate the real status.
 * @param {string} name Pokemon name.
 * @param {list} ivals Individual Values [H,A,B,C,D,S]
 * @param {list} evals Effort Values [H,A,B,C,D,S]
 * @param {string|number} seikaku seikaku identifier.
 * @param {number} lv Level.
 * @return {list} Real status. [H,A,B,C,D,S]
*/
function calculate_statistics(name, ivals, evals, seikaku, lv=50){
  var statistics = [0,0,0,0,0,0];
  if (name in POKENAME2DATA){
    if (typeof(seikaku)=="string"){
      seikaku = seikaku2id[seikaku];
    }
    var corr = seikakuid2corr[seikaku];
    poke_data = POKENAME2DATA[name]

    // Calculate Statistics.
    for (let i=0; i<6; i++){
      var base_stats = poke_data[Pdata_statusIdx+i];
      if (i==0){
        if (base_stats==1){
          val = 1
        }else{
          val = Math.floor((base_stats*2+ivals[i]+Math.floor(evals[i]/4))*lv/100)+10+lv
        }
      }else{
        val = Math.floor((Math.floor((base_stats*2+ivals[i]+Math.floor(evals[i]/4))*lv/100)+5)*corr[i-1])
      }
      statistics[i] = val;
    }
  }else{
    alert("ポケモン：" + name + "は対応していません。（エメラルドまでです。）")
  }
  return statistics
} 

/** 
 * Calculate the type & power of "hidden power"
 * @param {list} ivals Individual Values [H,A,B,C,D,S]
 * @return {list} [type, power] Hidden Power Information.
*/
function calculate_hidden_power(ivals){
  var mt=0, mp=0;
  ivals.forEach(function(e,i){
    if (e%2 === 1) mt += Math.pow(2, i);
    if (e%4 === 2 || e%4 === 3) mp += Math.pow(2, i);
  })
  var type = ["格","飛","毒","地","岩","虫","霊","鋼","炎","水","草","電","超","氷","竜","悪"][Math.floor(15*mt/63)];
  var power = 30 + Math.floor(40*mp/63);
  return [type, power]
}

const POKEWEATHERS = ["", "にほんばれ", "あめ", "すなあらし", "あられ"]

/**
 * Calculate the amount of damage in the third generation.
 * @ref https://wiki.xn--rckteqa2e.com/wiki/ダメージ
 * @ref https://wiki.xn--rckteqa2e.com/wiki/威力
 * @todo Calculation of moves that change the basic power.
 * @param {number} name Move Name.
 * @param {number} lv Level.
 * @param {number} Astats Stats on the Attacking side. [H,A,B,C,D,S]
 * @param {number} Dstats Stats on the Defending side. [H,A,B,C,D,S]
 * @param {list} Atypes Type on the Attacker side.
 * @param {list} Dtypes Type on the Defending side.
 * @param {list} Aability Ability on the Attacker side.
 * @param {list} Dability Ability on the Defending side.
 * @param {string} Aitem Item on the Attacker side.
 * @param {string} Ditem Item on the Defending side.
 * @param {number} AHP HP rate on the Attacker side.
 * @param {number} DHP HP rate on the Defending side.
 * @param {bool} helping_hand Whether partner uses "Helping Hand".
 * @param {bool} is_charged Whether you used "Charge" before.
 * @param {bool} is_mudsport_field 
 * @param {bool} is_watersport_field 
 * @param {bool} is_single Whether the battle is single.
 * @param {bool} has_shield Whether the Defender has shield such as "Light Screen" or "Reflect".
 * @param {bool} is_flash_fire Whether the Attacker's ability is "Flash Fire" and is activated.
 * @param {string} weather weather.
 * @param {bool} is_burned_state Whether the Attacker is in a burned state.
 * @param {bool} hit_critical Whether the move hit critical.
 * @return {list} [lb, ub] The range of Power.
*/
function calculate_damage(name, lv=50, 
                          Astats=[], Dstats=[], Atypes=[], Dtypes=[], Aability="", Dability="", 
                          Aitem="", Ditem="", Aranks=[], Dranks=[], AHP=1.0, DHP=1.0, 
                          helping_hand=false, is_charged=false, is_mudsport_field=false, is_watersport_field=false,
                          is_single=true, has_shield=false, is_flash_fire=false, 
                          weather="", is_burned_state=false, hit_critical=false){
  if (name in MOVENAME2DATA){
    var data = MOVENAME2DATA[name];
    var move_type = data[Mdata_typeIdx];
    var move_class = data[Mdata_classIdx];
    var attack_multiple = data[Mdata_rangeIdx] == "相手２匹";
    // Power correction
    var base_power = parseInt(data[Mdata_powerIdx]) || 0
    var power = (helping_hand ? 1.5 : 1)
              * base_power || 0
              * (POKETYPE2ITEM[move_type].indexOf(Aitem)!=-1 ? 1.1 : 1)
              * ((is_mudsport_field && move_type=="でんき") ? 0.5 : 1)
              * ((is_watersport_field && move_type=="ほのお") ? 0.5 : 1)
              * (AHP<=(1/3) ? ((Aability=="もうか" && move_type=="ほのお") || (Aability=="しんりょく" && move_type=="くさ") || (Aability=="げきりゅう" && move_type=="みず") || (Aability=="むしのしらせ" && move_type=="むし")) ? 1.5 : 1 : 1)
              * ((Dability=="あついしぼう" && ["ほのお", "こおり"].indexOf(move_type)!=-1) ? 0.5 : 1)
    // Rank compensation
    var A,D,Ar,Dr,Ma,Mb;
    if (move_class=="攻撃"){
      A=Astats[1]; D=Dstats[2]; Ar=Aranks[1]; Dr=Dranks[2];
    }else if (move_class=="特殊"){
      A=Astats[3]; D=Dstats[4]; Ar=Aranks[3]; Dr=Dranks[4]
    }else{
      console.log(name + "は「" + move_class + "」の技です。「攻撃」もしくは「特殊」の技でないと計算できません。")
      A=0;D=100;Ar=1;Dr=1;
    }
    Ar=RANK2MAGNIFICATION[Ar]; Dr=RANK2MAGNIFICATION[Dr];
    if (hit_critical){
      Ar=Math.max(1, Ar); Dr=Math.min(1, Dr)
    }
    // Damage compensation
    Ma = (hit_critical ? 1 : has_shield ? attack_multiple ? 2/3 : 0.5 : 1)
       * ((is_flash_fire && move_type=="ほのお") ? 1.5 : 1)
       * (weather=="あめ" ? move_type=="ほのお" ? 0.5 : move_type=="みず" ? 2 : name=="ソーラービーム" ? 0.5 : 1 :
           weather=="にほんばれ" ? move_type=="みず" ? 0.5 : move_type=="ほのお" ? 2 : 1 :
           (["すなあらし", "あられ"].indexOf(weather)!=-1 && name=="ソーラービーム") ? 0.5 : 1)
       * ((is_burned_state && move_class=="攻撃") ? 0.5 : 1)
       * ((!is_single && attack_multiple) ? 0.5 : 1)
    Mb = (hit_critical ? 2 : 1)
       * (Atypes.indexOf(move_type)!=-1 ? Aability=="てきおうりょく" ? 2 : 1.5 : 1)
       * (type_compatibility(move_type, Dtypes[0]) * type_compatibility(move_type, Dtypes[1]));
    var damage = Math.floor(Math.floor(Math.floor(Math.floor(lv*2/5+2)*power*A/D)/50*Ma+2)*Mb);
    var damages = arrange_damage(damage, name, base_power);
    // Charge
    if (is_charged && move_type=="でんき"){
      damages[0]*=2; damages[1]*=2;
    }
    return damages;
  }else{
    alert("技 " + name + " は対応していません。（エメラルドまでです。）")
  }
  function arrange_damage(damage, name, base_power){
    var damages;
    if (name=="ソニックブーム") damages=[20,20];
    else if (name=="りゅうのいかり") damages=[40,40];
    else if (name=="ちきゅうなげ") damages=[lv,lv];
    else if (name=="ナイトヘッド") damages=[lv,lv];
    else if (name=="サイコウェーブ") damages=[Math.floor(lv*0.5),Math.floor(lv*1.5)];
    else {
      if (base_power==0) damage = 0
      damages = [Math.floor(damage*0.85), damage];
    }
    return damages
  }
}