const Pdata_img_baseurl = "https://iwasakishuto.github.io/Front-End/tips/theme/images/pokemon/";
/**
 * pokemon name → <img>
 * @param {string} pname Pokemon Name.
 * @return {string} <img> wrapped by <div>.
*/
const Picon_img_create = function(pname){
  return `<div class='overflow-ms'><img src="${Pdata_img_baseurl+POKENAME2DATA[pname][Pdata_imgIdx]}" alt="${pname}"></div>`
}
/**
 * pokemon name → background-image style.
 * @param {string} pname Pokemon Name.
 * @return {string} "background-image: linear-gradient(...)"
*/
const Pname2typeColor = function(pname){
  let pdata = POKENAME2DATA[pname];
  let main_t = pdata[Pdata_typeIdx];
  let sub_t  = pdata[Pdata_typeIdx+1];
  let type_colors, type_str
  if (sub_t==""){
    type_colors = [TYPE2COLOR[main_t],TYPE2COLOR[main_t]];
    type_str = main_t
  }else{
    type_colors = [TYPE2COLOR[main_t], TYPE2COLOR[sub_t]];
    type_str = `${main_t}・${sub_t}`
  }
  let type_color = `linear-gradient(to right, ${type_colors[0]}88 20%, ${type_colors[1]}88 80%)`
  return {
    color: type_color, 
    string: type_str
  };
}

