/** 
 * Calculate the real status.
 * @param {string} name Pokemon name.
 * @param {list} ivals Individual Values [H,A,B,C,D,S]
 * @param {list} evals Effort Values [H,A,B,C,D,S]
 * @param {string|number} seikaku seikaku identifier.
 * @param {number} lv Level.
 * @return {list} Real status. [H,A,B,C,D,S]
*/
function calculate_statistics(name, ivals, evals, seikaku, lv=50){
  var statistics = [0,0,0,0,0,0];
  if (name in POKENAME2DATA){
    if (typeof(seikaku)=="string"){
      seikaku = seikaku2id[seikaku];
    }
    var corr = seikakuid2corr[seikaku];
    poke_data = POKENAME2DATA[name]

    // Calculate Statistics.
    for (let i=0; i<6; i++){
      var base_stats = poke_data[Pdata_statusIdx+i];
      if (i==0){
        if (base_stats==1){
          val = 1
        }else{
          val = Math.floor((base_stats*2+ivals[i]+Math.floor(evals[i]/4))*lv/100)+10+lv
        }
      }else{
        val = Math.floor((Math.floor((base_stats*2+ivals[i]+Math.floor(evals[i]/4))*lv/100)+5)*corr[i-1])
      }
      statistics[i] = val;
    }
  }else{
    alert("ポケモン：" + name + "は対応していません。（エメラルドまでです。）")
  }
  return statistics
} 

/** 
 * Calculate the type & power of "hidden power"
 * @param {list} ivals Individual Values [H,A,B,C,D,S]
 * @return {list} [type, power] Hidden Power Information.
*/
function calculate_hidden_power(ivals){
  var mt=0, mp=0;
  ivals.forEach(function(e,i){
    if (e%2 === 1) mt += Math.pow(2, i);
    if (e%4 === 2 || e%4 === 3) mp += Math.pow(2, i);
  })
  var type = ["格","飛","毒","地","岩","虫","霊","鋼","炎","水","草","電","超","氷","竜","悪"][Math.floor(15*mt/63)];
  var power = 30 + Math.floor(40*mp/63);
  return [type, power]
}

